import React from 'react';

import { mod } from "mathjs";
import NumberDisplay from "/home/kaappo/testit/canvas/src/components/numberdisplay.js"
import DrawHelper from './helpers';


class Wind extends React.Component {
    // constructor (props) {
    //     super(props);

    // }

    componentDidMount() {
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");

        this.state = {
            compassLineMaxLength: 10,
            canvas: canvas,
            ctx: ctx,
            drawHelper: new DrawHelper(canvas, ctx),

            origin: [canvas.width / 2, canvas.height / 2],
            radius: canvas.width / 2.1,

            closeHaulAngle: 30
        };

        console.log(this.state);

        // const canvas = this.state.canvas;

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.beginPath();
        ctx.arc(canvas.width / 2, canvas.height / 2, this.state.radius, 0, 2 * Math.PI);

        const divisions = [[24, 10], [72, 5]];
        for (const division of divisions) {
            this.drawDivision(division[0], division[1], division[0] === 24);
        }


        ctx.closePath();
        ctx.stroke();

        this.drawCloseHaulMarks();

        this.componentDidUpdate();
    }


    componentDidUpdate() {
        // const ctx = this.ctx;




        // let divisions = [12, 36, 72];
        // ctx.font = "20px Courier";
        // for (let index = 0; index < divisions.length; index++) {
        //     let division = divisions[index]
        //     this.drawDivision(division, ((divisions.length - index + 1) / divisions.length) ** 2 * 15, index === 0);
        // }
    }

    drawDivision(division, length, drawNumbers) {
        for (let i = 0; i < division; i++) {
            let baseAngle = 2 * Math.PI / division * i;
            // let angleOffset = this.props.heading * Math.PI / 180;
            let angleOffset = 0;

            this.state.drawHelper.drawCompassLine(this.state.origin, baseAngle + angleOffset, this.state.radius, length)
            if (drawNumbers) {
                let angleText = baseAngle / Math.PI * 180 <= 180 ? mod(baseAngle / Math.PI * 180, 360) : mod(360 - baseAngle / Math.PI * 180, 360);
                this.state.drawHelper.drawAngleText(this.state.origin, baseAngle + angleOffset, this.state.radius - length, angleText.toFixed(0), true)
            }
        }
    }

    drawCloseHaulMarks () {
        const ctx = this.state.ctx;
        ctx.beginPath();
        ctx.fillStyle = "rgba(0, 200, 0, 0.75)"
        ctx.arc(this.state.origin[0], this.state.origin[1], this.state.radius, -Math.PI / 2, /*-Math.PI / 4*/-Math.PI / 2 - Math.PI / 8, false);
        // ctx.arc(this.state.origin[0], this.state.origin[1], this.state.radius - this.state.compassLineMaxLength, -Math.PI / 4, -Math.PI / 2, true);
        ctx.closePath();
        ctx.fill();

        ctx.beginPath();
        ctx.fillStyle = "rgba(255, 100, 100, 0.75)"
        ctx.arc(this.state.origin[0], this.state.origin[1], this.state.radius, -Math.PI / 4 * 3, -Math.PI / 2, false);
        ctx.arc(this.state.origin[0], this.state.origin[1], this.state.radius - this.state.compassLineMaxLength, -Math.PI / 2 , - Math.PI / 4 * 3, true);
        ctx.closePath();
        ctx.fill();


    }


    // drawDivision(division, length, drawNumbers) {
    //     for (let i = 0; i < division; i++) {
    //         let baseAngle = 2 * Math.PI / division * i;
    //         let angleOffset = -this.props.heading * Math.PI / 180;


    //         this.drawHelper.drawCompassLine(this.origin, baseAngle + angleOffset, this.radius, length)
    //         if (drawNumbers) {
    //             let angleText = mod(baseAngle / Math.PI * 180, 360);
    //             this.drawHelper.drawAngleText(this.origin, baseAngle + angleOffset, this.radius - length, angleText.toFixed(0))
    //         }
    //     }
    // }
    render() {
        return <div className="container" style={{ width: this.props.width + "px", height: this.props.height + "px" }}>
            {/* <NumberDisplay
                className="number"
                value={this.props.heading}
                suffix="Â°"
                unit="T"
                width={this.props.width}
                height={this.props.height / 3 * 2}
                upperBound={360}
                decimalPlaces={1}
                fontSize={this.props.width / 4}
            /> */}
            <canvas ref="canvas" className="compassRose" width={this.props.width} height={this.props.height} />
        </div>
    }
}

export default Wind
