import React from 'react';
import logo from './logo.svg';
import './App.css';
import Compass from "./components/compass"



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {heading: 0};
  }

  componentDidMount() {
    this.interval = setInterval(() => this.setState((state) => {
      return {heading: (this.state.heading + 0.0212) % 360};
    }), 100);
  }

  render() {
    return (
      <div>
        <Compass width={400} height={400} heading={this.state.heading}/>
      </div>
    );
  }
}

export default App;
