import React from 'react';

import { mod } from "mathjs";
import "/home/kaappo/testit/canvas/src/components/compass.css"
import NumberDisplay from "/home/kaappo/testit/canvas/src/components/numberdisplay.js"

import DrawHelper from "./helpers.js"

class Compass extends React.Component {
    constructor(props) {
        super(props);

        // this.props = {
        //     width: props.width,
        //     height: props.height,
        //     heading: props.heading,
        // };

        this.arcCenterOffsetY = this.props.width / 2.6;
        this.radius = this.getCircleRadius();
        console.log(this.props.height + this.arcCenterOffsetY, this.radius)
        // this.origin = [this.props.width / 2, 0];

        this.state = {
            heading: 0
        }

        this.onMessage = (message) => {
            // console.log("Received message: " + JSON.stringify(message) + ", " + this.radius);
            const extracted = message.values[0].value / Math.PI * 180;
            if (message.source.label === "nmeaFromFile") {
                this.setState({heading: extracted});
            }
        }

        this.props.subscribe(["navigation.courseOverGroundTrue"], this.onMessage)
    }



    getCircleRadius () {
        return Math.sqrt((this.props.width / 2) ** 2 + this.arcCenterOffsetY ** 2)
    }

    componentDidMount() {
        this.canvas = this.refs.canvas;
        this.ctx = this.canvas.getContext("2d");
        this.compassLineMaxLength = this.canvas.width / 2 / 10


        this.origin = [this.canvas.width / 2, this.canvas.height + this.arcCenterOffsetY];

        this.drawHelper = new DrawHelper(this.canvas, this.ctx);
        this.componentDidUpdate();        
    }


    componentDidUpdate () {
        const ctx = this.ctx;

        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        ctx.beginPath();
        ctx.arc(this.props.width / 2, this.canvas.height + this.arcCenterOffsetY, this.radius, 0, 2 * Math.PI);
        ctx.fillRect(this.props.width / 2 - 1, this.canvas.height - this.radius + this.arcCenterOffsetY - 10, 2, 10)


        let divisions = [[24, 1], [72, 2], [144, 3]];
        ctx.font = this.canvas.width / 20 + "px Courier";
        for (let index = 0; index < divisions.length; index++) {
            let division = divisions[index]
            // this.drawDivision(division[0], this.compassLineMaxLength / division[1], index === 0);
            let angleProvider = (index) => {
                let baseAngle = 2 * Math.PI / division[0] * index;
                let angleOffset = this.state.heading * Math.PI / 180;
                // console.log(baseAngle, angleOffset)
                return baseAngle - angleOffset;
            }

            let numberTextProvider = (index) => {
                if (division[0] === 24) {
                    return mod(angleProvider(index) / Math.PI * 180, 360).toFixed(0);

                    // this.drawHelper.drawAngleText(this.origin, baseAngle + angleOffset, this.radius - 2 * length, angleText.toFixed(0), false)
                } else {
                    return "";
                }
            }
            // console.log(this.origin, this.radius, division)
            this.drawHelper.drawDivision(this.origin, this.radius, division[0], division[1], angleProvider, numberTextProvider)
        }

        ctx.closePath();
        ctx.stroke();
    }

    // drawDivision (division, length, drawNumbers) {
    //     for (let i = 0; i < division; i++) {
            // let baseAngle = 2 * Math.PI / division * i;
            // let angleOffset = -this.state.heading * Math.PI / 180;


    //         this.drawHelper.drawCompassLine(this.origin, baseAngle + angleOffset, this.radius, length)
            // if (drawNumbers) {
            //     let angleText = mod(baseAngle / Math.PI * 180, 360);
            //     this.drawHelper.drawAngleText(this.origin, baseAngle + angleOffset, this.radius - 2* length, angleText.toFixed(0), false)
            // }
    //     }
    // }

    render () {
        return <div className="container" style={{width: this.props.width + "px", height: this.props.height + "px"}}>
            <NumberDisplay 
                className="number" 
                value={this.state.heading} 
                suffix="Â°" 
                unit="T" 
                width={this.props.width} 
                height={this.props.height / 3 * 2} 
                upperBound={360} 
                decimalPlaces={1} 
                fontSize={this.props.width / 4} 
            />            
            <canvas ref="canvas" className="compassRose" width={this.props.width} height={this.props.height / 2} />
        </div>
    }
}

export default Compass
