import React from 'react';

import { mod } from "mathjs";

const cheese = "https://upload.wikimedia.org/wikipedia/commons/a/a4/Kaastekoop.jpg"

class Compass extends React.Component {
    constructor(props) {
        super(props);

        this.props = {
            width: props.width,
            height: props.height,
            heading: props.heading,
        };

        this.arcCenterOffsetY = 100;
        this.radius = this.getCircleRadius();
    }

    getCircleRadius () {
        return Math.sqrt((this.props.width / 2) ** 2 + this.arcCenterOffsetY ** 2)
    }

    componentDidMount() {
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");

        ctx.rect(0, 0, this.props.width, this.props.height)
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        // img.onload = () => {
            // ctx.drawImage(img, 0, 0);
        ctx.font = "40px Courier";
        ctx.fillText(this.props.heading.toFixed(2), this.props.width / 2, this.props.height);

        ctx.beginPath();
        ctx.arc(this.props.width / 2, this.props.height + this.arcCenterOffsetY, this.radius, 0, 2 * Math.PI);
        ctx.fillRect(this.props.width / 2 - 1, this.props.height / 2 - 10, 2, 10)

        let divisions = [12, 36, 72];
        ctx.font = "20px Courier";
        for (let index = 0; index < divisions.length; index++) {
            let division = divisions[index]
            for (let i = 0; i < division; i++) {
                let baseAngle =  2 * Math.PI / division * i;
                let angleOffset = this.props.heading * Math.PI / 180;


                // ctx.fillText(this.props.heading.toFixed(2), start[0] + 5, start[1] + 5)
                this.drawCompassLine(ctx, baseAngle + angleOffset, 1 / (index + 2) ** 2)
                if (index == 0) {
                    let angleText = mod(180 - baseAngle / Math.PI * 180, 360)
                    this.drawAngleText(ctx, baseAngle + angleOffset, angleText.toFixed(0))
                }
            }
        }
        ctx.closePath();
        ctx.stroke();
        // }
    }

    modulus 

    getPosFromAngle (angle) {
        return [this.props.width / 2 + Math.sin(angle) * this.radius, this.props.height + this.arcCenterOffsetY + Math.cos(angle) * this.radius]
    }

    drawAngleText (ctx, angle, text) {
        let pos = this.getPosFromAngle(angle)
        let x = pos[0];
        let y = pos[1];

        ctx.save();
        ctx.translate(x + 50 * Math.sin(angle - Math.PI), y + 50 * Math.cos(angle - Math.PI));
        ctx.rotate(-angle + Math.PI);

        ctx.textAlign = 'center';
        ctx.fillText(text, 0, 10);

        ctx.restore();

    }

    drawCompassLine (ctx, angle, length) {
        let start = [this.props.width / 2 + Math.sin(angle) * this.radius /0.9, this.props.height + this.arcCenterOffsetY + Math.cos(angle) * this.radius / 1.1]
        let end = this.getPosFromAngle(angle)

        ctx.moveTo(start[0], start[1]);
        ctx.lineTo(end[0], end[1]);
    }

    componentDidUpdate () {
        this.componentDidMount();
    }

    render () {
        return <div>
            <canvas ref="canvas" width={this.props.width} height={this.props.height} />

            <img ref="image" src={cheese} className="hidden" />
        </div>
    }
}

export default Compass
