class DrawHelper {
    constructor (canvas, ctx) {
        this.canvas = canvas;
        this.ctx = ctx;
        // this.radius = radius;
        // this.arcCenterOffsetY = arcCenterOffsetY;
    }

    drawAngleText (origin, angle, radius, text, dontRotateText) {
        if (dontRotateText === undefined) {
            dontRotateText = false;
        }

        this.ctx.save();

        let pos = this.getPosFromAngle(origin, angle, radius)
        let x = pos[0];
        let y = pos[1];

        if (!dontRotateText) {
            this.ctx.rotate(-angle + Math.PI);
        }
        this.ctx.translate(x, y);

        this.ctx.textAlign = 'center';
        this.ctx.fillText(text, 0, 10);

        this.ctx.restore();

    }

    getPosFromAngle(origin, angle, radius) {
        let x = origin[0]
        let y = origin[1]
        return [x + Math.sin(angle) * radius, y + Math.cos(angle) * radius]
    }

    drawCompassLine(origin, angle, radius, length) {
        let end = this.getPosFromAngle(origin, angle, radius)
        let start = this.getPosFromAngle(origin, angle, radius - length)

        this.ctx.moveTo(start[0], start[1]);
        this.ctx.lineTo(end[0], end[1]);
    }
}


export default DrawHelper
