class DrawHelper {
    constructor () {

    }

    drawAngleText (ctx, angle, text) {
        let pos = this.getPosFromAngle(angle, 0)
        let x = pos[0];
        let y = pos[1];

        ctx.save();
        ctx.translate(x + 40 * Math.sin(angle - Math.PI), y + 40 * Math.cos(angle - Math.PI));
        ctx.rotate(-angle + Math.PI);

        ctx.textAlign = 'center';
        ctx.fillText(text, 0, 10);

        ctx.restore();

    }

    getPosFromAngle(angle, offset) {
        return [this.canvas.width / 2 + Math.sin(angle) * this.radius - offset * Math.sin(angle), this.canvas.height + this.arcCenterOffsetY + Math.cos(angle) * this.radius - offset * Math.cos(angle)]
    }
}


export default DrawHelper
