import  React from "react";

import Compass from "./compass"
import Wind from "./wind"


const server_root = "ws://localhost:3000"

class Instruments extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            data: null
        }

        this.ws = new WebSocket(server_root + "/signalk/v1/stream/?subscribe=none")

        this.subscribers = []

    }

    componentDidMount () {
        const preparePath = (name) => {
            return {
                "path": name,
                // "period": 1000,
                "format": "full",
                // "policy": "ideal",
                // "minPeriod": 200
            }
        }

        this.ws.onopen = () => {
            console.log("open")
            this.ws.send(JSON.stringify({
                "context": "vessels.self",
                "subscribe": [
                    preparePath("environment.depth.belowTransducer"),
                    preparePath("environment.wind"),
                    preparePath("navigation.speedThroughWater"),
                    preparePath("navigation.headingTrue"),
                    preparePath("navigation.position"),
                ]
            }))
        }

        this.ws.onmessage = (event) => {
            console.log(event)
            const message = JSON.parse(event.data);
            this.setState({data: message});
            console.log(message);

            for (const subscriber of this.subscribers) {
                try {
                    // console.log(message.updates[0].values[0])
                    // console.log(subscriber.paths)
                    if (subscriber.paths === message.updates[0].values[0].path) {
                        subscriber.callback(message)
                    }
                } catch (err) {
                    // console.error(message)
                    // console.error(err)
                }
            }
        }


        console.log(this.refs);

        this.ws.onclose = () => {
            // this.ws.send(JSON.stringify({
            //     "context": "*",
            //     "unsubscribe": [{
            //         "path": "*"
            //     }]
            // }))
        }

        setInterval(() => {
            this.ws.send(JSON.stringify({
                "context": "*",
                "unsubscribe": [{
                    "path": "*"
                }]
            }));
            this.ws.close()
        }, 1000)
    }


    render () {
        const setCallback = (paths, callback) => {
            this.subscribers.push({paths: paths, callback: callback})
        }

        return (        
            <div>
                <div className="table">
                    <Wind ref="wind" width={250} height={250} subscribe={setCallback} />
                    <Compass ref="compass" width={250} height={250} heading={0} subscribe={setCallback} />
                </div>
                <div className="table">
                    <Wind ref="wind2" width={500} height={500} subscribe={setCallback}/>
                </div>
            </div>
        );
    }
}

export default Instruments
