export function createDeprecatedImport() {
  return function deprecatedImport() {
    throw new Error('The global import function is not available anymore in v6.0.0. 
' + 'Please create a mathjs instance if you want to import functions. 
' + 'Example:
' + '
' + '  import { create, all } from 'mathjs';
' + '  const mathjs = create(all);
' + '  mathjs.import(...);
');
  };
}
