import React from 'react';

import { mod } from "mathjs";
import NumberDisplay from "/home/kaappo/testit/canvas/src/components/numberdisplay.js"
import DrawHelper from './helpers';


class Wind extends React.Component {

    componentDidMount() {
        this.canvas = this.refs.canvas;
        this.ctx = this.canvas.getContext("2d");
        this.drawHelper = new DrawHelper(this.canvas, this.ctx);

        const ctx = this.ctx;

        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        ctx.beginPath();
        ctx.arc(this.canvas.width / 2, this.canvas.height / 2, this.canvas.width / 2, 0, 2 * Math.PI);

        const divisions = [24, 72];

        ctx.closePath();
        ctx.stroke();

        this.componentDidUpdate();
    }


    componentDidUpdate() {
        // const ctx = this.ctx;




        // let divisions = [12, 36, 72];
        // ctx.font = "20px Courier";
        // for (let index = 0; index < divisions.length; index++) {
        //     let division = divisions[index]
        //     this.drawDivision(division, ((divisions.length - index + 1) / divisions.length) ** 2 * 15, index === 0);
        // }
    }

    drawDivision(division, length, drawNumbers) {
        for (let i = 0; i < division; i++) {
            let baseAngle = 2 * Math.PI / division * i;
            let angleOffset = this.props.heading * Math.PI / 180;


            this.drawHelper.drawCompassLine(baseAngle + angleOffset, length)
            if (drawNumbers) {
                let angleText = mod(180 - baseAngle / Math.PI * 180, 360);
                this.drawHelper.drawAngleText(baseAngle + angleOffset, angleText.toFixed(0))
            }
        }
    }

    render() {
        return <div className="container" style={{ width: this.props.width + "px", height: this.props.height + "px" }}>
            {/* <NumberDisplay
                className="number"
                value={this.props.heading}
                suffix="Â°"
                unit="T"
                width={this.props.width}
                height={this.props.height / 3 * 2}
                upperBound={360}
                decimalPlaces={1}
                fontSize={this.props.width / 4}
            /> */}
            <canvas ref="canvas" className="compassRose" width={this.props.width} height={this.props.height} />
        </div>
    }
}

export default Wind
