class DrawHelper {
    constructor (canvas, ctx) {
        this.canvas = canvas;
        this.ctx = ctx;
        // this.radius = radius;
        // this.arcCenterOffsetY = arcCenterOffsetY;
    }

    drawAngleText (origin, angle, text) {
        let pos = this.getPosFromAngle(angle, 0)
        let x = pos[0];
        let y = pos[1];

        this.ctx.save();
        this.ctx.translate(x + 40 * Math.sin(angle - Math.PI), y + 40 * Math.cos(angle - Math.PI));
        this.ctx.rotate(-angle + Math.PI);

        this.ctx.textAlign = 'center';
        this.ctx.fillText(text, 0, 10);

        this.ctx.restore();

    }

    getPosFromAngle(origin, angle, radius) {
        let x = origin[0]
        let y = origin[1]
        //this.canvas.height + this.arcCenterOffsetY
        return [x + Math.sin(angle) * radius, y + Math.cos(angle) * radius]
    }

    drawCompassLine(origin, angle, radius, length) {
        let end = this.getPosFromAngle(angle, 0)
        let start = this.getPosFromAngle(angle, length)

        this.ctx.moveTo(start[0], start[1]);
        this.ctx.lineTo(end[0], end[1]);
    }
}


export default DrawHelper
