import React from 'react';

const cheese = "https://upload.wikimedia.org/wikipedia/commons/a/a4/Kaastekoop.jpg"

class Compass extends React.Component {
    constructor(props) {
        super(props);

        this.props = {
            width: props.width,
            height: props.height,
            heading: props.heading
        };
    }

    componentDidMount() {
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        // img.onload = () => {
            // ctx.drawImage(img, 0, 0);
        ctx.font = "40px Courier";
        ctx.fillText(this.props.heading.toFixed(2), this.props.width / 2, this.props.height);

        ctx.beginPath();
        ctx.arc(this.props.width / 2, this.props.height, this.props.width / 2, 0, 2 * Math.PI);
        ctx.rect(0, 0, this.props.width, this.props.height)

        let division = 8;
        ctx.font = "20px Courier";

        for (let i = 0; i < division; i++) {
            let angle =  2 * Math.PI / division * i + this.props.heading * Math.PI/180;
            console.log(angle)


            // ctx.fillText(this.props.heading.toFixed(2), start[0] + 5, start[1] + 5)
            this.drawCompassLine(ctx, angle, 0.5)
            this.drawAngleText(ctx, angle)
        }
        ctx.stroke();
        ctx.closePath();
        // }
    }

    getPosFromAngle (angle) {
        return [this.props.width / 2 + Math.sin(angle) * this.props.width / 2, this.props.height + Math.cos(angle) * this.props.width / 2]
    }

    drawAngleText (ctx, angle) {
        let pos = this.getPosFromAngle(angle)
        let x = pos[0];
        let y = pos[1];

        ctx.save();
        ctx.translate(x + 50 * Math.sin(angle - Math.PI), y + 50 * Math.cos(angle - Math.PI));
        ctx.rotate(-angle + Math.PI);

        ctx.textAlign = 'center';
        ctx.fillText((angle / Math.PI * 180).toFixed(0), 0, 10);

        ctx.restore();

    }

    drawCompassLine (ctx, angle, length) {
        let start = [this.props.width / 2 + Math.sin(angle) * this.props.width / (2 + length), this.props.height + Math.cos(angle) * this.props.width / (2 + length)]
        let end = [this.props.width / 2 + Math.sin(angle) * this.props.width / 2, this.props.height + Math.cos(angle) * this.props.width / 2]

        ctx.moveTo(start[0], start[1]);
        ctx.lineTo(end[0], end[1]);
    }

    componentDidUpdate () {
        this.componentDidMount();
    }

    render () {
        return <div>
            <canvas ref="canvas" width={this.props.width} height={this.props.height} />

            <img ref="image" src={cheese} className="hidden" />
        </div>
    }
}

export default Compass
