import  React from "react";

import Compass from "./compass"
import Wind from "./wind"


const server_root = "ws://localhost:3000"

class Instruments extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            data: null
        }

        this.ws = new WebSocket(server_root + "/signalk/v1/stream/?subscribe=self")

    }

    componentDidMount () {
        this.ws.onopen = () => {
            console.log("open")
            this.ws.send(JSON.stringify({
                "context": "vessels.self",
                "subscribe": [
                    {
                    "path": "navigation.speedThroughWater",
                    "period": 1000,
                    "format": "delta",
                    "policy": "ideal",
                    "minPeriod": 200
                }]
            }))
        }

        this.ws.onmessage = (event) => {
            const message = JSON.parse(event.data);
            this.setState({data: message});
            console.log(message);
        }

        this.ws.onclose = () => {
            this.ws.send(JSON.stringify({
                "context": "*",
                "unsubscribe": [{
                    "path": "*"
                }]
            }))
        }

        setInterval(() => {
            this.ws.send(JSON.stringify({
                "context": "*",
                "unsubscribe": [{
                    "path": "*"
                }]
            }))
        }, 1000)
    }


    render () {
        return (        
            <div>
                <div className="table">
                    <Wind width={250} height={250} />
                    <Compass width={250} height={250} heading={0} />
                </div>
                <div className="table">
                    <Wind width={500} height={500} />
                </div>
            </div>
        );
    }
}

export default Instruments
