import React from 'react';

import "/home/kaappo/testit/canvas/src/components/numberdisplay.css";

class NumberDisplay extends React.Component {
    constructor(props) {
        super(props);

        this.setState({
            value: props.value,
            suffix: props.suffix,
            unit: props.unit,
            upperBound: props.upperBound,
            decimalPlaces: props.decimalPlaces,

            width: props.width,
            height: props.height,
            fontSize: props.fontSize
        });
    }

    componentDidMount () {
        this.canvas = this.refs.canvas;
        const ctx = this.canvas.getContext("2d");
        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height)

        ctx.font = this.state.fontsize + "px Courier"

        let measure = this.state.upperBound.toFixed(this.state.decimalPlaces) + this.state.suffix + " " + this.state.unit
        let text1 = this.state.value.toFixed(0);
        let text2 = "." + ((this.state.value - Math.trunc(this.state.value)) * 10 ** this.state.decimalPlaces).toFixed(0) + this.state.suffix + " " + this.state.unit

        // let size = ctx.measureText(measure);
        // console.log(size)
        // ctx.fillText(text, (this.canvas.width - size.width) / 2, (this.canvas.height + size.actualBoundingBoxAscent) / 2)
        let width = ctx.measureText(text1).width
        ctx.fillText(text1, this.canvas.width / 2 - width, this.canvas.height / 2)
        ctx.font = "40px Courier"
        ctx.fillText(text2, this.canvas.width / 2, this.canvas.height / 2)

    }

    componentDidUpdate () {
        this.componentDidMount()
    }

    render() {
        console.log(this.state)

        return (
            <div className="displayContainer">
                <canvas ref="canvas" width={this.state.width} height={this.state.height}/>
            </div>
        );
    }
}

export default NumberDisplay;
