const drawAngleText = function (ctx, angle, text) {
    let pos = this.getPosFromAngle(angle, 0)
    let x = pos[0];
    let y = pos[1];

    ctx.save();
    ctx.translate(x + 40 * Math.sin(angle - Math.PI), y + 40 * Math.cos(angle - Math.PI));
    ctx.rotate(-angle + Math.PI);

    ctx.textAlign = 'center';
    ctx.fillText(text, 0, 10);

    ctx.restore();

}
