class DrawHelper {
    constructor (canvas, ctx, arcCenterOffsetY, radius) {
        this.canvas = canvas;
        this.ctx = ctx;
        this.arcCenterOffsetY = arcCenterOffsetY;
        this.radius = radius;
    }

    drawAngleText (angle, text) {
        let pos = this.getPosFromAngle(angle, 0)
        let x = pos[0];
        let y = pos[1];

        ctx.save();
        ctx.translate(x + 40 * Math.sin(angle - Math.PI), y + 40 * Math.cos(angle - Math.PI));
        ctx.rotate(-angle + Math.PI);

        ctx.textAlign = 'center';
        ctx.fillText(text, 0, 10);

        ctx.restore();

    }

    getPosFromAngle(angle, offset) {
        console.log(angle, offset)
        return [this.canvas.width / 2 + Math.sin(angle) * this.radius - offset * Math.sin(angle), this.canvas.height + this.arcCenterOffsetY + Math.cos(angle) * this.radius - offset * Math.cos(angle)]
    }

    drawCompassLine(angle, length) {
        let end = this.getPosFromAngle(angle, 0)
        let start = this.getPosFromAngle(angle, length)

        this.ctx.moveTo(start[0], start[1]);
        this.ctx.lineTo(end[0], end[1]);
    }
}


export default DrawHelper
