import React from 'react';

import { mod } from "mathjs";
import "/home/kaappo/testit/canvas/src/components/compass.css"
import NumberDisplay from "/home/kaappo/testit/canvas/src/components/numberdisplay.js"


class Compass extends React.Component {
    constructor(props) {
        super(props);

        this.props = {
            width: props.width,
            height: props.height,
            heading: props.heading,
        };

        this.arcCenterOffsetY = 110;
        this.radius = this.getCircleRadius();

        this.numberDisplay = React.createRef();
    }

    getCircleRadius () {
        return Math.sqrt((this.props.width / 2) ** 2 + this.arcCenterOffsetY ** 2)
    }

    componentDidMount() {

        this.canvas = this.refs.canvas;
        const ctx = this.canvas.getContext("2d");

        ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        // img.onload = () => {
            // ctx.drawImage(img, 0, 0);
        ctx.font = "40px Courier";
        ctx.textAlign = "center";
        ctx.fillText(this.props.heading.toFixed(2), this.props.width / 2, this.canvas.height);

        ctx.beginPath();
        ctx.arc(this.props.width / 2, this.canvas.height + this.arcCenterOffsetY, this.radius, 0, 2 * Math.PI);
        ctx.fillRect(this.props.width / 2 - 1, this.canvas.height - this.radius + this.arcCenterOffsetY - 10, 2, 10)

        let divisions = [12, 36, 72];
        ctx.font = "20px Courier";
        for (let index = 0; index < divisions.length; index++) {
            let division = divisions[index]
            for (let i = 0; i < division; i++) {
                let baseAngle =  2 * Math.PI / division * i;
                let angleOffset = this.props.heading * Math.PI / 180;


                // ctx.fillText(this.props.heading.toFixed(2), start[0] + 5, start[1] + 5)
                this.drawCompassLine(ctx, baseAngle + angleOffset, ((divisions.length - index + 1) / divisions.length) ** 2 * 15)
                if (index === 0) {
                    let angleText = mod(180 - baseAngle / Math.PI * 180, 360)
                    this.drawAngleText(ctx, baseAngle + angleOffset, angleText.toFixed(0))
                }
            }
        }

        ctx.closePath();
        ctx.stroke();
        // }
    }


    getPosFromAngle (angle, offset) {
        return [this.canvas.width / 2 + Math.sin(angle) * this.radius - offset * Math.sin(angle), this.canvas.height + this.arcCenterOffsetY + Math.cos(angle) * this.radius - offset * Math.cos(angle)]
    }

    drawAngleText (ctx, angle, text) {
        let pos = this.getPosFromAngle(angle, 0)
        let x = pos[0];
        let y = pos[1];

        ctx.save();
        ctx.translate(x + 40 * Math.sin(angle - Math.PI), y + 40 * Math.cos(angle - Math.PI));
        ctx.rotate(-angle + Math.PI);

        ctx.textAlign = 'center';
        ctx.fillText(text, 0, 10);

        ctx.restore();

    }

    drawCompassLine (ctx, angle, length) {
        let end = this.getPosFromAngle(angle, 0)
        let start = this.getPosFromAngle(angle, length)

        ctx.moveTo(start[0], start[1]);
        ctx.lineTo(end[0], end[1]);
    }

    componentDidUpdate () {
        this.componentDidMount();
    }

    render () {
        return <div className="container">
            <NumberDisplay value={this.props.heading} suffix="Â°" unit="T" width={this.props.width} height={100} upperLimit={360} />            
            <canvas ref="canvas" className="compassRose" width={this.props.width} height={this.props.height / 3} />
        </div>
    }
}

export default Compass
